import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  position: relative;
  width: 100%;
`;

const EditableDiv = styled.div`
  width: 100%;
  min-height: 110px;
  font-size: 1.13rem;
  padding: 0.9rem 1.1rem 0.4rem 1.1rem;
  background: transparent;
  color: #222;
  font-family: inherit;
  outline: none;
  resize: vertical;
  white-space: pre-wrap;
  word-break: break-word;
`;

const Underline = styled.div`
  position: absolute;
  left: 1.1rem;
  right: 1.1rem;
  height: 2px;
  background: #e0e0e0;
  transition: top 0.1s;
`;

export default function CustomTextarea({ value, onChange, placeholder, minHeight = 110, ...props }) {
  const divRef = useRef();
  const underlineRef = useRef();

  // Place the underline under the last line
  useEffect(() => {
    const div = divRef.current;
    const underline = underlineRef.current;
    if (!div || !underline) return;
    // Create a range to get the last line position
    let range = document.createRange();
    let sel = window.getSelection();
    if (div.childNodes.length > 0) {
      let lastNode = div.childNodes[div.childNodes.length - 1];
      range.setStart(lastNode, lastNode.length || 0);
    } else {
      range.setStart(div, 0);
    }
    range.collapse(true);
    let rect = range.getBoundingClientRect();
    let divRect = div.getBoundingClientRect();
    let top = rect.bottom - divRect.top;
    // Fallback if empty
    if (!value) {
      top = parseFloat(getComputedStyle(div).paddingTop) + 24;
    }
    underline.style.top = `${top}px`;
  }, [value]);

  // Handle input
  const handleInput = e => {
    onChange && onChange({
      target: {
        value: e.target.innerText
      }
    });
  };

  return (
    <Wrapper style={{ minHeight }}>
      <EditableDiv
        ref={divRef}
        contentEditable
        suppressContentEditableWarning
        spellCheck={true}
        aria-label={placeholder}
        onInput={handleInput}
        onBlur={handleInput}
        {...props}
      >
        {value || ''}
      </EditableDiv>
      <Underline ref={underlineRef} />
    </Wrapper>
  );
}
