import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  position: relative;
  width: 100%;
`;

const EditableDiv = styled.div`
  width: 100%;
  min-height: 1.8em;
  font-size: 1.13rem;
  padding: 0.2rem 1.1rem 0.2rem 1.1rem;
  background: transparent;
  color: #222;
  font-family: inherit;
  outline: none;
  resize: none;
  white-space: pre-wrap;
  word-break: break-word;
`;

const Underline = styled.div`
  position: absolute;
  left: 1.1rem;
  right: 1.1rem;
  height: 2px;
  background: #e0e0e0;
  transition: top 0.1s;
`;

export default function CustomTextarea({ value, onChange, placeholder, minHeight = 1.8, ...props }) {
  const divRef = useRef();
  const underlineRef = useRef();

  useEffect(() => {
    const div = divRef.current;
    const underline = underlineRef.current;
    if (!div || !underline) return;
    // Calculate the height of the content
    const lineHeight = parseFloat(getComputedStyle(div).lineHeight);
    const numLines = (div.innerText.match(/\n/g) || []).length + 1;
    const top = div.offsetTop + (numLines * lineHeight) + 0.2 * lineHeight;
    underline.style.top = `${top}px`;
  }, [value]);

  const handleInput = e => {
    onChange && onChange({
      target: {
        value: e.target.innerText
      }
    });
  };

  return (
    <Wrapper style={{ minHeight: `${minHeight}em` }}>
      <EditableDiv
        ref={divRef}
        contentEditable
        suppressContentEditableWarning
        spellCheck={true}
        aria-label={placeholder}
        onInput={handleInput}
        onBlur={handleInput}
        {...props}
      >
        {value || ''}
      </EditableDiv>
      <Underline ref={underlineRef} />
    </Wrapper>
  );
}
