import React from 'react';
import styled from 'styled-components';

const WhatWeDoBlock = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 8rem 0 0 0;
`;

const WhatWeDoText = styled.p`
  font-size: 1.97rem;
  font-weight: 400;
  color: #222;
  text-align: left;
  max-width: 700px;
  margin: 0 auto 2.5rem auto;
`;

const HeroHeading = styled.h1`
  font-family: 'JetBrains Mono', 'Fira Mono', 'Menlo', 'monospace';
  font-size: 2.7rem;
  font-weight: 700;
  color: #222;
  margin: 0 0 1.1rem 0;
  line-height: 1.08;
  letter-spacing: -0.01em;
  .hero-grey {
    color: #b3b3b3;
    font-weight: 400;
  }
  .hero-black {
    color: #222;
    font-weight: 700;
  }
  @media (max-width: 700px) {
    font-size: 1.5rem;
  }
`;

const HeroSubtitle = styled.p`
  font-size: 1.13rem;
  color: #555;
  margin: 0 0 1.7rem 0;
  font-weight: 400;
  max-width: 420px;
  @media (max-width: 700px) {
    font-size: 1.01rem;
    margin-bottom: 1.1rem;
  }
`;

const HeroHeadingText = styled.span`
  font-family: 'Inter', 'system-ui', 'Arial', sans-serif;
  font-size: clamp(2.1rem, 4vw, 3.2rem);
  font-weight: 400;
  color: #222;
  line-height: 1.13;
  text-align: left;
  max-width: 1200px;
  display: block;
`;

const HeroSubtle = styled.span`
  color: #b3b3b3;
`;

const HERO_HEADING = (
  <WhatWeDoText>
    We build software, systems, and content for teams that want control and results.
  </WhatWeDoText>
);

const HERO_SUBTITLE = '';

const Section = styled.section`
  max-width: 700px;
  margin: 4.5rem auto 0 auto;
  padding: 0 15px;
`;

const SectionHeading = styled.h2`
  font-size: 2rem;
  font-weight: 700;
  color: #222;
  margin: 0 0 1.2rem 0;
`;

const SectionText = styled.p`
  font-size: 1.18rem;
  color: #222;
  margin: 0 0 2.2rem 0;
  line-height: 1.6;
`;

const About = () => (
  <>
    <WhatWeDoBlock>
      {HERO_HEADING}
    </WhatWeDoBlock>
    <Section>
      <SectionHeading>Custom AI</SectionHeading>
      <SectionText>
        We design and build AI solutions tailored to your business, giving you full control over your data, models, and deployment. From private LLMs to workflow automation, we help you unlock the power of AI on your terms.
      </SectionText>
    </Section>
    <Section>
      <SectionHeading>Development</SectionHeading>
      <SectionText>
        Our engineering team delivers robust, scalable web and software solutions. We handle everything from backend APIs to modern frontends, ensuring your product is fast, secure, and ready to grow.
      </SectionText>
    </Section>
    <Section>
      <SectionHeading>Automation</SectionHeading>
      <SectionText>
        We automate repetitive tasks and complex workflows, integrating your tools and data so you can focus on what matters. Our automations are reliable, secure, and built to fit your unique needs.
      </SectionText>
    </Section>
    <Section>
      <SectionHeading>Production</SectionHeading>
      <SectionText>
        From video to digital content, we help you create, launch, and scale your brand's story. Our production team brings ideas to life with creativity, speed, and technical excellence.
      </SectionText>
    </Section>
    <Section>
      <SectionHeading>Design</SectionHeading>
      <SectionText>
        We craft digital experiences that are beautiful, intuitive, and effective. Our design team works closely with you to turn ideas into products people love to use.
      </SectionText>
    </Section>
  </>
);

export default About;
