import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';

const FooterWrapper = styled.footer`
  width: 100%;
  padding: 2rem 0 1.5rem 0;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2.5rem;
  background: #fff;
  color: #222;
  font-size: 1.08rem;
  font-weight: 500;
  border-top: 1px solid #eee;
  @media (max-width: 600px) {
    flex-direction: column;
    gap: 1.1rem;
    font-size: 0.98rem;
  }
`;

const FooterLink = styled.a`
  color: #222;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  &:hover {
    color: #184B54;
  }
`;

const XIcon = styled.svg`
  width: 1.5em;
  height: 1.5em;
  display: inline-block;
  vertical-align: middle;
  fill: currentColor;
`;

const slideUpOut = keyframes`
  0% { opacity: 1; transform: translateY(0); }
  100% { opacity: 0; transform: translateY(-16px); }
`;
const slideUpIn = keyframes`
  0% { opacity: 0; transform: translateY(16px); }
  100% { opacity: 1; transform: translateY(0); }
`;

const EmailCopyWrapper = styled.span`
  display: inline-block;
  position: relative;
  min-width: 120px;
  height: 1.2em;
`;
const EmailText = styled.span`
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  transition: opacity 0.2s;
  ${({ animateOut }) =>
    animateOut &&
    css`
      animation: ${slideUpOut} 0.35s forwards;
    `}
  ${({ show }) =>
    !show &&
    css`
      opacity: 0;
      pointer-events: none;
    `}
`;
const EmailCopied = styled.span`
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  color: #184B54;
  font-weight: 600;
  ${({ animateIn }) =>
    animateIn &&
    css`
      animation: ${slideUpIn} 0.35s forwards;
    `}
  ${({ show }) =>
    !show &&
    css`
      opacity: 0;
      pointer-events: none;
    `}
`;

const Footer = () => {
  const [copied, setCopied] = useState(false);
  const [animating, setAnimating] = useState(false);
  const handleCopy = (e) => {
    e.preventDefault();
    if (copied || animating) return;
    navigator.clipboard.writeText('Hey@hepta.no');
    setAnimating(true);
    setTimeout(() => {
      setCopied(true);
      setAnimating(false);
      setTimeout(() => setCopied(false), 1200);
    }, 350);
  };
  return (
    <FooterWrapper>
      <FooterLink href="https://x.com/HeptaCreative" target="_blank" rel="noopener noreferrer" aria-label="HeptaCreative on X">
        <XIcon viewBox="0 0 24 24" aria-hidden="true">
          <path d="M17.53 3.00049H21.001L14.431 10.29L22.111 21.0005H15.609L10.797 14.6025L5.391 21.0005H1.929L8.929 13.21L1.609 3.00049H8.25L12.609 8.80149L17.53 3.00049ZM16.34 19.0005H18.13L7.75 4.00049H5.859L16.34 19.0005Z" />
        </XIcon>
      </FooterLink>
      <span>
        <FooterLink href="#" onClick={handleCopy} tabIndex={0} aria-label="Copy email to clipboard">
          <EmailCopyWrapper>
            <EmailText show={!copied} animateOut={animating && !copied}>Hey@hepta.no</EmailText>
            <EmailCopied show={copied} animateIn={copied}>Email copied!</EmailCopied>
          </EmailCopyWrapper>
        </FooterLink>
      </span>
      <span>Oslo</span>
    </FooterWrapper>
  );
};

export default Footer;
