import React from 'react';
import styled from 'styled-components';

const HERO_HEADING = (
  <>
    <span className="hero-grey">Hey!</span> <span className="hero-black">Production</span>
    <br />
    <span className="hero-grey">(0â†’1)</span> <span className="hero-black">content</span>
  </>
);
const HERO_SUBTITLE = 'We create, edit, and deliver high-quality media.';
const HERO_BUTTON = "Let's Shoot";
const HERO_IMAGE_ALT = 'Production Illustration';

const HeroSection = styled.section`
  position: relative;
  min-height: 70vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #fafbfa;
  padding: 6vw 0 2vw 0;
  overflow: visible;
  @media (max-width: 900px) {
    flex-direction: column;
    align-items: center;
    min-height: 0;
    padding: 2.5rem 0 1.5rem 0;
  }
`;

const HeroContentBlock = styled.div`
  position: relative;
  z-index: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  background: #fff;
  border-radius: 22px;
  box-shadow: 0 4px 32px 0 rgba(24,75,84,0.07);
  padding: 3.5rem 3.5rem 2.5rem 3.5rem;
  min-width: 0;
  max-width: 540px;
  margin-right: 3vw;
  margin-left: 40px;
  @media (max-width: 900px) {
    margin: 0 auto 2.5rem auto;
    padding: 2.2rem 1.2rem 1.5rem 1.2rem;
    max-width: 98vw;
    align-items: center;
    text-align: center;
  }
`;

const FloatingIcon = styled.div`
  position: absolute;
  left: 0;
  top: -2.7rem;
  width: 54px;
  height: 54px;
  background: #fff;
  border-radius: 16px;
  box-shadow: 0 2px 12px rgba(24,75,84,0.10);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2.1rem;
  color: #184B54;
  z-index: 2;
  @media (max-width: 900px) {
    left: 50%;
    transform: translateX(-50%);
    top: -2.7rem;
  }
`;

const HeroHeading = styled.h1`
  font-family: 'JetBrains Mono', 'Fira Mono', 'Menlo', 'monospace';
  font-size: 2.7rem;
  font-weight: 700;
  color: #222;
  margin: 0 0 1.1rem 0;
  line-height: 1.08;
  letter-spacing: -0.01em;
  .hero-grey {
    color: #b3b3b3;
    font-weight: 400;
  }
  .hero-black {
    color: #222;
    font-weight: 700;
  }
  @media (max-width: 700px) {
    font-size: 1.5rem;
  }
`;

const HeroSubtitle = styled.p`
  font-size: 1.13rem;
  color: #555;
  margin: 0 0 1.7rem 0;
  font-weight: 400;
  max-width: 420px;
  @media (max-width: 700px) {
    font-size: 1.01rem;
    margin-bottom: 1.1rem;
  }
`;

const HeroButton = styled.a`
  background: #fff;
  color: #222;
  font-size: 1.1rem;
  font-family: 'JetBrains Mono', 'Fira Mono', 'Menlo', 'monospace';
  font-weight: 600;
  border: none;
  border-radius: 14px;
  padding: 0.85rem 2.1rem;
  text-decoration: none;
  cursor: pointer;
  display: inline-block;
  box-shadow: 0 2px 12px rgba(24,75,84,0.10);
  transition: background 0.18s, color 0.18s;
  margin-top: 1.1rem;
  &:hover {
    background: #184B54;
    color: #fff;
  }
`;

const HeroImageWrapper = styled.div`
  flex: 1;
  display: flex;
  align-items: flex-start;
  justify-content: flex-end;
  min-width: 0;
  @media (max-width: 900px) {
    position: static;
    margin-top: 2.2rem;
    width: 100%;
    justify-content: center;
  }
`;

const HeroImage = styled.div`
  width: 390px;
  height: 440px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #bbb;
  font-size: 1.2rem;
  font-weight: 500;
  @media (max-width: 700px) {
    width: 90vw;
    max-width: 340px;
    height: 260px;
  }
`;

const Production = () => (
  <main>
    <HeroSection>
      <HeroContentBlock>
        <FloatingIcon>ðŸŽ¬</FloatingIcon>
        <HeroHeading>{HERO_HEADING}</HeroHeading>
        <HeroSubtitle>{HERO_SUBTITLE}</HeroSubtitle>
        <HeroButton href="mailto:hello@example.com">{HERO_BUTTON}</HeroButton>
      </HeroContentBlock>
      <HeroImageWrapper>
        <HeroImage aria-label={HERO_IMAGE_ALT}>[image]</HeroImage>
      </HeroImageWrapper>
    </HeroSection>
  </main>
);

export default Production;
