import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';

const FooterWrapper = styled.footer`
  width: 100%;
  padding: 2rem 0 1.5rem 0;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2.5rem;
  background: #fff;
  color: #222;
  font-size: 1.08rem;
  font-weight: 500;
  border-top: 1px solid #eee;
  @media (max-width: 600px) {
    flex-direction: column;
    gap: 1.1rem;
    font-size: 0.98rem;
  }
`;

const FooterLink = styled.a`
  color: #222;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  &:hover {
    color: #184B54;
  }
`;

const XIcon = styled.svg`
  width: 1.5em;
  height: 1.5em;
  display: inline-block;
  vertical-align: middle;
  fill: currentColor;
`;

const roll = keyframes`
  0% { transform: rotateX(0deg); opacity: 1; }
  49% { opacity: 0; }
  50% { transform: rotateX(90deg); opacity: 0; }
  51% { opacity: 0; }
  100% { transform: rotateX(0deg); opacity: 1; }
`;

const EmailSpan = styled.span`
  display: inline-block;
  min-width: 120px;
  perspective: 400px;
  ${({ animate }) =>
    animate &&
    css`
      animation: ${roll} 0.7s;
    `}
`;

const Footer = () => {
  const [copied, setCopied] = useState(false);
  const handleCopy = (e) => {
    e.preventDefault();
    navigator.clipboard.writeText('Hey@hepta.no');
    setCopied(true);
    setTimeout(() => setCopied(false), 1500);
  };
  return (
    <FooterWrapper>
      <FooterLink href="https://x.com/HeptaCreative" target="_blank" rel="noopener noreferrer" aria-label="HeptaCreative on X">
        <XIcon viewBox="0 0 24 24" aria-hidden="true">
          <path d="M17.53 3.00049H21.001L14.431 10.29L22.111 21.0005H15.609L10.797 14.6025L5.391 21.0005H1.929L8.929 13.21L1.609 3.00049H8.25L12.609 8.80149L17.53 3.00049ZM16.34 19.0005H18.13L7.75 4.00049H5.859L16.34 19.0005Z" />
        </XIcon>
      </FooterLink>
      <span>
        <FooterLink href="#" onClick={handleCopy} tabIndex={0} aria-label="Copy email to clipboard">
          <EmailSpan animate={copied}>{copied ? 'Email copied!' : 'Hey@hepta.no'}</EmailSpan>
        </FooterLink>
      </span>
      <span>Oslo</span>
    </FooterWrapper>
  );
};

export default Footer;
