import React from 'react';
import Layout from '../components/Layout';
import Hero from '../components/Hero';
import styled from 'styled-components';
import { COLORS } from '../styles/colors';

const Section = styled.section`
  margin: 3rem 0;
`;

const ClientsRow = styled.div`
  display: flex;
  gap: 2rem;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin: 2rem 0 3rem 0;
`;

const ClientLogo = styled.div`
  width: 120px;
  height: 48px;
  background: ${COLORS.offWhite};
  border-radius: 0.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  font-size: 1.1rem;
  color: #222;
`;

const ServicesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 auto 2rem auto;
  max-width: 600px;
  font-size: 1.3rem;
  color: #222;
`;

const ServiceItem = styled.li`
  margin: 0.5rem 0;
  display: flex;
  align-items: center;
  &::before {
    content: '○';
    color: ${COLORS.darkTeal};
    margin-right: 0.75rem;
    font-size: 1.1em;
  }
`;

const WorkGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  gap: 2rem;
  margin: 2rem 0 3rem 0;
`;

const WorkCard = styled.div`
  background: ${COLORS.offWhite};
  border-radius: 1rem;
  min-height: 220px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-end;
  padding: 1.5rem;
  font-size: 1.1rem;
  font-weight: 500;
  color: #222;
`;

const Testimonial = styled.blockquote`
  font-size: 1.5rem;
  font-style: italic;
  color: #222;
  margin: 3rem 0 2rem 0;
  max-width: 700px;
`;

const Home = () => (
  <>
    <Hero />
    <Layout>
      <Section>
        <h2>We design + build</h2>
        <ServicesList>
          <ServiceItem>Software systems</ServiceItem>
          <ServiceItem>AI-powered tools</ServiceItem>
          <ServiceItem>Product websites</ServiceItem>
          <ServiceItem>Design systems</ServiceItem>
          <ServiceItem>Digital strategies</ServiceItem>
          <ServiceItem>Brand narratives</ServiceItem>
        </ServicesList>
      </Section>
      <Section>
        <h2>Our Work</h2>
        <WorkGrid>
          <WorkCard>CoinTracker (placeholder image)</WorkCard>
          <WorkCard>Sierra (placeholder image)</WorkCard>
          <WorkCard>Google Deepmind (placeholder image)</WorkCard>
          <WorkCard>Pio (placeholder image)</WorkCard>
          <WorkCard>Phantom (placeholder image)</WorkCard>
          <WorkCard>Ramp (placeholder image)</WorkCard>
        </WorkGrid>
      </Section>
      <Section>
        <Testimonial>
          “BB’s excellence in strategy, design, and advanced technology is undeniable. They’re not just experts within their field but also run a tight process and make things happen. They are genuinely nice people whom I would recommend as a partner any day.”
          <footer style={{fontSize: '1rem', marginTop: '1rem', fontStyle: 'normal'}}>— Kaave Pour, Co-founder and Head of SPACE10</footer>
        </Testimonial>
      </Section>
      <Section>
        <h2>Want to work with us?</h2>
        <p>We’d love to hear from you! <a href="mailto:hello@example.com">Get in touch</a></p>
      </Section>
    </Layout>
  </>
);

export default Home;
