import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';

const FooterWrapper = styled.footer`
  width: 100%;
  min-height: 320px;
  padding: 7rem 0 5rem 0;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  background: #fff;
  color: #222;
  font-size: 1.08rem;
  font-weight: 500;
  border-top: 1px solid #eee;
`;

const FooterInner = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 2.5rem;
  @media (max-width: 600px) {
    gap: 1.1rem;
    font-size: 0.98rem;
  }
`;

const FooterLink = styled.a`
  color: #222;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  &:hover {
    color: #184B54;
  }
`;

const XIcon = styled.svg`
  width: 1.5em;
  height: 1.5em;
  display: inline-block;
  vertical-align: middle;
  fill: currentColor;
`;

const slideUp = keyframes`
  0% { transform: translateY(0); }
  100% { transform: translateY(-100%); }
`;
const slideDown = keyframes`
  0% { transform: translateY(100%); }
  100% { transform: translateY(0); }
`;
const slideBackDown = keyframes`
  0% { transform: translateY(0); }
  100% { transform: translateY(100%); }
`;
const slideBackUp = keyframes`
  0% { transform: translateY(-100%); }
  100% { transform: translateY(0); }
`;

const EmailCopyWrapper = styled.span`
  display: inline-block;
  position: relative;
  min-width: 120px;
  height: 1.2em;
  overflow: hidden;
`;
const EmailText = styled.span`
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  ${({ animateUp, animateDown }) =>
    animateUp
      ? css`animation: ${slideUp} 0.4s cubic-bezier(0.4, 0.2, 0.2, 1) forwards;`
      : animateDown
      ? css`animation: ${slideBackUp} 0.4s cubic-bezier(0.4, 0.2, 0.2, 1) forwards;`
      : ''}
  ${({ show }) =>
    !show &&
    css`
      pointer-events: none;
      visibility: hidden;
    `}
`;
const EmailCopied = styled.span`
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  color: #184B54;
  font-weight: 600;
  ${({ animateIn, animateOut }) =>
    animateIn
      ? css`animation: ${slideDown} 0.4s cubic-bezier(0.4, 0.2, 0.2, 1) forwards;`
      : animateOut
      ? css`animation: ${slideBackDown} 0.4s cubic-bezier(0.4, 0.2, 0.2, 1) forwards;`
      : ''}
  ${({ show }) =>
    !show &&
    css`
      pointer-events: none;
      visibility: hidden;
    `}
`;

const Footer = () => {
  const [copied, setCopied] = useState(false);
  const [animState, setAnimState] = useState('idle'); // idle | up | down
  const handleCopy = (e) => {
    e.preventDefault();
    if (copied || animState !== 'idle') return;
    navigator.clipboard.writeText('Hey@hepta.no');
    setAnimState('up');
    setTimeout(() => {
      setCopied(true);
      setAnimState('idle');
      setTimeout(() => {
        setAnimState('down');
        setTimeout(() => {
          setCopied(false);
          setAnimState('idle');
        }, 320);
      }, 2000);
    }, 320);
  };
  return (
    <FooterWrapper>
      <FooterInner>
        <FooterLink href="https://x.com/HeptaCreative" target="_blank" rel="noopener noreferrer" aria-label="HeptaCreative on X">
          <XIcon viewBox="0 0 24 24" aria-hidden="true">
            <path d="M17.53 3.00049H21.001L14.431 10.29L22.111 21.0005H15.609L10.797 14.6025L5.391 21.0005H1.929L8.929 13.21L1.609 3.00049H8.25L12.609 8.80149L17.53 3.00049ZM16.34 19.0005H18.13L7.75 4.00049H5.859L16.34 19.0005Z" />
          </XIcon>
        </FooterLink>
        <FooterLink href="#" onClick={handleCopy} tabIndex={0} aria-label="Copy email to clipboard">
          <EmailCopyWrapper>
            <EmailText
              show={!copied}
              animateUp={animState === 'up'}
              animateDown={animState === 'down'}
            >
              Hey@hepta.no
            </EmailText>
            <EmailCopied
              show={copied}
              animateIn={animState === 'up'}
              animateOut={animState === 'down'}
            >
              Email copied!
            </EmailCopied>
          </EmailCopyWrapper>
        </FooterLink>
        <span>Oslo</span>
      </FooterInner>
    </FooterWrapper>
  );
};

export default Footer;
