import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  position: relative;
  width: 100%;
`;

const EditableDiv = styled.div`
  width: 100%;
  min-height: 110px;
  font-size: 1.13rem;
  padding: 0.9rem 1.1rem 0.4rem 1.1rem;
  background: transparent;
  color: #222;
  font-family: inherit;
  outline: none;
  resize: vertical;
  white-space: pre-wrap;
  word-break: break-word;
`;

export default function CustomTextarea({ value, onChange, placeholder, minHeight = 110, ...props }) {
  const divRef = useRef();

  // Handle input
  const handleInput = e => {
    onChange && onChange({
      target: {
        value: e.target.innerText
      }
    });
  };

  return (
    <Wrapper style={{ minHeight }}>
      <EditableDiv
        ref={divRef}
        contentEditable
        suppressContentEditableWarning
        spellCheck={true}
        aria-label={placeholder}
        onInput={handleInput}
        onBlur={handleInput}
        {...props}
      >
        {value || ''}
      </EditableDiv>
    </Wrapper>
  );
}
