import React from 'react';
import styled from 'styled-components';
import { COLORS } from '../styles/colors';



const JobSection = styled.section`
  max-width: 800px;
  margin: 7rem auto 0 auto;
  padding: 4rem 0;
`;

const JobSectionHeading = styled.h2`
  font-size: 1.5rem;
  color: #666;
  font-weight: 500;
  margin-bottom: 3rem;
`;

const JobList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const JobItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem 0;
  border-bottom: 1px solid #f0f0f0;

  &:last-child {
    border-bottom: none;
  }

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
    gap: 0.5rem;
  }
`;

const JobTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: #222;
  margin: 0;
`;

const JobLocations = styled.div`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
`;

const Location = styled.span`
  font-size: 1rem;
  color: #666;
  font-weight: 400;
`;



const CTAButton = styled.button`
  background: #222;
  color: #fff;
  font-size: 1.1rem;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  padding: 1rem 2rem;
  cursor: pointer;
  transition: background 0.2s ease;
  margin-top: 2rem;

  &:hover {
    background: #444;
  }
`;

const Careers = () => {
  const jobs = [
    {
      title: 'Creative Lead',
      locations: ['Oslo', 'Barcelona', 'Amsterdam']
    },
    {
      title: 'Lead Product Designer',
      locations: ['Oslo', 'London', 'Barcelona', 'Amsterdam']
    },
    {
      title: 'Senior Creative Strategist',
      locations: ['Oslo', 'Barcelona', 'Amsterdam']
    },
    {
      title: 'Senior Motion Designer',
      locations: ['Oslo', 'Barcelona', 'Amsterdam']
    },
    {
      title: 'Senior Product Manager',
      locations: ['Oslo', 'Barcelona', 'Amsterdam']
    },
    {
      title: 'Open application',
      locations: ['Oslo', 'Barcelona', 'Amsterdam']
    }
  ];



  return (
    <>
      <JobSection>
        <JobSectionHeading>Open positions</JobSectionHeading>
        <JobList>
          {jobs.map((job, index) => (
            <JobItem key={index}>
              <JobTitle>{job.title}</JobTitle>
              <JobLocations>
                {job.locations.map((location, locIndex) => (
                  <Location key={locIndex}>{location}</Location>
                ))}
              </JobLocations>
            </JobItem>
          ))}
        </JobList>
        <CTAButton>Get in touch</CTAButton>
      </JobSection>

      <CultureGallery>
        <GalleryContainer>
          <GalleryGrid>
            {cultureImages.map((image, index) => (
              <GalleryImage key={index}>
                <img src={image} alt={`Company culture ${index + 1}`} />
              </GalleryImage>
            ))}
          </GalleryGrid>
        </GalleryContainer>
      </CultureGallery>
    </>
  );
};

export default Careers;
