import React from 'react';
import styled from 'styled-components';
import { COLORS } from '../styles/colors';

const HeroSection = styled.section`
  width: 100vw;
  margin-left: 50%;
  transform: translateX(-50%);
  background: #fff;
  min-height: 70vh;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding: 0;
`;

const HeroBg = styled.div`
  position: absolute;
  top: 0; left: 0; right: 0; bottom: 0;
  width: 100%;
  height: 100%;
  background: #ddd url('/assets/images/hero-placeholder.jpg') center/cover no-repeat;
  z-index: 1;
  opacity: 0.92;
`;

const HeroContent = styled.div`
  position: relative;
  z-index: 2;
  padding: 3.5rem 2rem 2.5rem 2rem;
  max-width: 1100px;
  margin-left: 0;
`;

const HeroTitle = styled.h1`
  font-size: 2.8rem;
  font-weight: 700;
  color: #222;
  margin: 0 0 1.5rem 0;
  line-height: 1.1;
`;

const HeroSubtitle = styled.p`
  font-size: 1.5rem;
  color: #222;
  margin: 0;
  font-weight: 400;
`;

const Hero = () => (
  <HeroSection>
    <HeroBg />
    <HeroContent>
      <HeroTitle>Bakken & BÃ¦ck is a design and technology studio. We build digital products, from zero to launch.</HeroTitle>
      <HeroSubtitle>We design + build software systems, AI-powered tools, product websites, design systems, digital strategies, brand narratives and more.</HeroSubtitle>
    </HeroContent>
  </HeroSection>
);

export default Hero;
