import React from 'react';
import styled from 'styled-components';
import { COLORS } from '../styles/colors';



const JobSection = styled.section`
  max-width: 600px;
  margin: 7rem auto 0 auto;
  padding: 4rem 0;
`;

const JobSectionHeading = styled.h2`
  font-size: clamp(1.4rem, 3vw, 2.2rem);
  color: #b3b3b3;
  font-weight: 600;
  margin-bottom: 0.4em;
  text-align: left;
  margin-left: 1px;
`;

const TopLine = styled.div`
  width: 100%;
  height: 1px;
  background: #222;
  margin-bottom: 2rem;
`;

const JobList = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const JobItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem 0;
  border-bottom: 1px solid #222;
  cursor: pointer;
  transition: background 0.18s;

  &:hover {
    background: #f8f8f8;
  }

  &:last-child {
    border-bottom: 1px solid #222;
  }
`;

const JobTitle = styled.h3`
  font-size: 1.7rem;
  font-weight: 500;
  color: #222;
  margin: 0;
  line-height: 1.15;
`;

const ArrowIcon = styled.span`
  font-size: 1.2rem;
  color: #222;
  font-weight: 400;
`;



const CTAButton = styled.button`
  background: #222;
  color: #fff;
  font-size: 1.1rem;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  padding: 1rem 2rem;
  cursor: pointer;
  transition: background 0.2s ease;
  margin-top: 2rem;

  &:hover {
    background: #444;
  }
`;

const Careers = () => {
  const jobs = [
    'Creative Lead',
    'Lead Product Designer',
    'Senior Creative Strategist',
    'Senior Motion Designer',
    'Senior Product Manager',
    'Open application'
  ];



  return (
    <>
      <JobSection>
        <JobSectionHeading>Open positions</JobSectionHeading>
        <JobList>
          {jobs.map((job, index) => (
            <JobItem key={index}>
              <JobTitle>{job.title}</JobTitle>
              <JobLocations>
                {job.locations.map((location, locIndex) => (
                  <Location key={locIndex}>{location}</Location>
                ))}
              </JobLocations>
            </JobItem>
          ))}
        </JobList>
        <CTAButton>Get in touch</CTAButton>
      </JobSection>
    </>
  );
};

export default Careers;
