import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  width: 100vw;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: #fff;
  position: relative;
  overflow: hidden;
  padding-top: 80px; /* to account for sticky navbar */
`;

const Headline = styled.h1`
  font-family: 'Inter', 'system-ui', 'Arial', sans-serif;
  font-size: clamp(2.8rem, 7vw, 6.2rem);
  font-weight: 800;
  color: #222;
  text-align: center;
  letter-spacing: -0.03em;
  line-height: 1.05;
  margin: 0 0 1.5rem 0;
  max-width: 900px;
`;

const Subheadline = styled.p`
  font-family: 'Inter', 'system-ui', 'Arial', sans-serif;
  font-size: clamp(1.2rem, 2.5vw, 2.1rem);
  font-weight: 400;
  color: #222;
  text-align: center;
  line-height: 1.3;
  margin: 0 0 2.5rem 0;
  max-width: 600px;
`;

const CTAButton = styled.a`
  display: inline-block;
  background: #222;
  color: #fff;
  font-size: 1.15rem;
  font-weight: 500;
  border-radius: 6px;
  padding: 0.85rem 2.2rem;
  text-decoration: none;
  transition: background 0.2s;
  margin-top: 0.5rem;
  &:hover {
    background: #184B54;
  }
`;

// Placeholder for hero image/video (if any)
const HeroMediaPlaceholder = styled.div`
  width: 100vw;
  max-width: 1200px;
  height: 320px;
  background: #f4f4f4;
  border-radius: 24px;
  margin: 2.5rem auto 0 auto;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.3rem;
  color: #bbb;
  font-weight: 600;
  box-shadow: 0 2px 24px 0 rgba(0,0,0,0.04);
`;

const Hero = () => (
  <HeroSection>
    <Headline>
      We help you design, build, and launch digital products.
    </Headline>
    <Subheadline>
      A digital product studio for ambitious teams. We turn ideas into world-class products, from concept to launch and beyond.
    </Subheadline>
    <CTAButton href="#work">See our work</CTAButton>
    <HeroMediaPlaceholder>
      [ Hero Image/Video Placeholder ]
    </HeroMediaPlaceholder>
  </HeroSection>
);

export default Hero;
