import React, { useRef } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  position: relative;
  width: 100%;
`;

const EditableDiv = styled.div`
  width: 100%;
  min-height: 2.6em;
  font-size: 1.13rem;
  line-height: 1.5;
  padding: 1.1rem 1.1rem 0.7rem 1.1rem;
  background: transparent;
  color: #222;
  font-family: inherit;
  outline: none;
  resize: none;
  white-space: pre-wrap;
  word-break: break-word;
  box-sizing: border-box;
`;

const Underline = styled.div`
  position: absolute;
  left: 1.1rem;
  right: 1.1rem;
  bottom: 0;
  height: 2px;
  background: #e0e0e0;
`;

export default function CustomTextarea({ value, onChange, placeholder, minHeight = 2.6, ...props }) {
  const divRef = useRef();

  const handleInput = e => {
    onChange && onChange({
      target: {
        value: e.target.innerText
      }
    });
  };

  return (
    <Wrapper style={{ minHeight: `${minHeight}em` }}>
      <EditableDiv
        ref={divRef}
        contentEditable
        suppressContentEditableWarning
        spellCheck={true}
        aria-label={placeholder}
        onInput={handleInput}
        onBlur={handleInput}
        {...props}
      >
        {value || ''}
      </EditableDiv>
      <Underline />
    </Wrapper>
  );
}
