import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import CustomTextarea from '../components/CustomTextarea';
import { FaArrowLeft } from 'react-icons/fa';

const CenteredPage = styled.div`
  min-height: 100vh;
  width: 100vw;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #fafbfa;
`;

const FormWrapper = styled.div`
  max-width: 420px;
  width: 100%;
  margin: 0 auto;
  /* background: #fff; */
  /* border-radius: 18px; */
  /* box-shadow: 0 4px 32px 0 rgba(24,75,84,0.07); */
  padding: 2.5rem 2.2rem 2.2rem 2.2rem;
  display: flex;
  flex-direction: column;
  align-items: stretch;
  box-sizing: border-box;
  text-align: left;
`;

const StepTitle = styled.h2`
  font-size: 1.35rem;
  font-weight: 500;
  color: #222;
  margin: 0 0 1.2rem 0;
`;

const Input = styled.input`
  width: 100%;
  box-sizing: border-box;
  font-size: 1.13rem;
  padding: 1.1rem 1.1rem 0.7rem 1.1rem;
  border: none;
  border-bottom: 2px solid #e0e0e0;
  border-radius: 0;
  background: transparent;
  color: #222;
  font-family: inherit;
  min-height: 2.6em;
  resize: none;
  box-shadow: none;
  outline: none;
  margin-bottom: 1.7rem;
  &:focus {
    border-bottom: 2px solid #e0e0e0;
    background: transparent;
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  font-size: 1.13rem;
  padding: 0.9rem 1.1rem 0.4rem 1.1rem;
  border: none;
  border-radius: 0;
  background: transparent;
  color: #222;
  font-family: inherit;
  min-height: 110px;
  resize: vertical;
  box-shadow: none;
  outline: none;
`;

const Button = styled.button`
  background: none;
  color: #222;
  font-size: 1.13rem;
  font-weight: 600;
  border: none;
  border-radius: 0;
  padding: 0;
  margin-top: 2.5rem;
  margin-left: 0;
  align-self: flex-start;
  cursor: pointer;
  transition: color 0.18s;
  box-shadow: none;
  &:hover {
    color: #184B54;
  }
`;

const Progress = styled.div`
  font-size: 0.98rem;
  color: #b3b3b3;
  margin-bottom: 1.2rem;
`;

const Confirmation = styled.div`
  font-size: 1.18rem;
  color: #184B54;
  margin-top: 2.5rem;
  text-align: center;
`;

const StepHeaderWrapper = styled.div`
  min-height: 4.5rem;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`;

const StepHeaderRow = styled.div`
  display: flex;
  align-items: center;
  gap: 0.7rem;
`;

const BackArrowButton = styled.button`
  background: none;
  border: none;
  padding: 0;
  margin: 0;
  color: #b3b3b3;
  font-size: 1.1rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  transition: color 0.18s;
  &:hover {
    color: #222;
  }
`;

const FieldArea = styled.div`
  min-height: 4.5rem;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`;

const steps = [
  {
    label: 'What is your company name?',
    type: 'text',
    name: 'company',
    placeholder: 'Company name',
  },
  {
    label: stepData => `How many employees does ${stepData.company || 'your company'} have?`,
    type: 'number',
    name: 'employees',
    placeholder: 'Number of employees',
  },
  {
    label: 'Tell us about your project.',
    type: 'textarea',
    name: 'project',
    placeholder: 'Describe your project',
  },
  {
    label: 'What is the best way to get in touch with you? (Please enter your email address.)',
    type: 'email',
    name: 'email',
    placeholder: 'you@email.com',
  },
];

export default function Contact() {
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({ company: '', employees: '', project: '', email: '' });
  const [submitted, setSubmitted] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleNext = e => {
    e.preventDefault();
    if (step < steps.length - 1) {
      setStep(step + 1);
    } else {
      setSubmitted(true);
    }
  };

  if (submitted) {
    return (
      <CenteredPage>
        <FormWrapper>
          <Confirmation>
            Thank you for reaching out!<br />
            We'll get back to you soon.
          </Confirmation>
          <Button style={{marginTop: '2.5rem'}} onClick={() => navigate('/')}>Back to Home</Button>
        </FormWrapper>
      </CenteredPage>
    );
  }

  const currentStep = steps[step];
  const label = typeof currentStep.label === 'function' ? currentStep.label(form) : currentStep.label;

  return (
    <CenteredPage>
      <FormWrapper as="form" onSubmit={handleNext} autoComplete="off">
        <StepHeaderWrapper>
          <StepHeaderRow>
            {step > 0 && (
              <BackArrowButton type="button" onClick={() => setStep(step - 1)} aria-label="Go back">
                <FaArrowLeft />
              </BackArrowButton>
            )}
            <Progress>Step {step + 1} of {steps.length}</Progress>
          </StepHeaderRow>
          <StepTitle>{label}</StepTitle>
        </StepHeaderWrapper>
        <FieldArea>
          {currentStep.type === 'textarea' ? (
            <CustomTextarea
              value={form[currentStep.name]}
              onChange={e => handleChange({ target: { name: currentStep.name, value: e.target.value } })}
              placeholder={currentStep.placeholder}
              minHeight={2.6}
              name={currentStep.name}
              required
            />
          ) : (
            <Input
              type={currentStep.type}
              name={currentStep.name}
              value={form[currentStep.name]}
              onChange={handleChange}
              placeholder={currentStep.placeholder}
              required
            />
          )}
        </FieldArea>
        <Button type="submit">{step === steps.length - 1 ? 'Submit' : 'Next'}</Button>
      </FormWrapper>
    </CenteredPage>
  );
}
