import React from 'react';
import styled from 'styled-components';

// Colors for the stripes (approximate to Bakken & Bæck)
const STRIPES = [
  '#4A90E2', // blue
  '#F5D547', // yellow
  '#E6E3F7', // light purple
  '#F44F3B', // orange
  '#2D2553', // dark purple
  '#FFFCCB', // pale yellow
  '#5FC39B', // green
  '#F7D6DE', // pink
];

const HeroSection = styled.section`
  width: 100vw;
  min-height: 100vh;
  background: #fff;
  display: flex;
  flex-direction: column;
  align-items: stretch;
  justify-content: flex-start;
  padding: 0;
  margin: 0;
  position: relative;
  overflow-x: hidden;
`;

const VideoPlaceholder = styled.div`
  width: 100vw;
  height: 48vw;
  max-height: 520px;
  min-height: 320px;
  display: flex;
  flex-direction: row;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  border-bottom: 0;
  @media (max-width: 900px) {
    height: 60vw;
    min-height: 180px;
    max-height: 320px;
  }
`;

const Stripe = styled.div`
  flex: 1 1 0;
  height: 100%;
`;

const HeroTextBlock = styled.div`
  width: 100vw;
  background: #fff;
  display: flex;
  justify-content: flex-start;
  align-items: flex-end;
  padding: 0;
  margin: 0;
  min-height: 90px;
`;

const HeroTextInner = styled.div`
  font-family: 'Inter', 'system-ui', 'Arial', sans-serif;
  font-size: clamp(1.5rem, 3vw, 2.5rem);
  font-weight: 400;
  color: #222;
  line-height: 1.13;
  padding: 1.2rem 0 0.7rem 2.5vw;
  @media (max-width: 600px) {
    font-size: 1.1rem;
    padding-left: 4vw;
    padding-right: 2vw;
  }
`;

const BrandSpan = styled.span`
  color: #b3b3b3;
  font-weight: 700;
`;

const AmpersandSpan = styled.span`
  color: #b3b3b3;
  font-weight: 700;
  font-family: inherit;
`;

const Hero = () => (
  <HeroSection>
    <VideoPlaceholder aria-label="Video placeholder">
      {STRIPES.map((color, i) => (
        <Stripe key={i} style={{ background: color }} />
      ))}
    </VideoPlaceholder>
    <HeroTextBlock>
      <HeroTextInner>
        <BrandSpan>Bakken </BrandSpan>
        <AmpersandSpan>&amp; Bæck</AmpersandSpan>
        {' is a design and technology studio. We build digital products, from zero to launch.'}
      </HeroTextInner>
    </HeroTextBlock>
  </HeroSection>
);

export default Hero;
