import React from 'react';
import Layout from '../components/Layout';
import Hero from '../components/Hero';
import styled from 'styled-components';
import { COLORS } from '../styles/colors';

const Section = styled.section`
  margin: 3rem 0;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ServicesBlock = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: -20px;
`;

const BigHeading = styled.h2`
  color: #b3b3b3;
  font-size: clamp(1.4rem, 3vw, 2.2rem);
  font-weight: 600;
  margin-bottom: 0.4em;
  text-align: left;
  margin-left: 1px;
`;

const ServicesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 0 2.5rem 0;
  width: 100%;
  max-width: 480px;
`;

const ServiceButton = styled.button`
  background: none;
  border: none;
  padding: 0;
  margin: 0;
  width: 100%;
  text-align: left;
  cursor: pointer;
  display: flex;
  align-items: center;
  font-size: 1.7rem;
  font-weight: 500;
  line-height: 1.15;
  color: #222;
  outline: none;
`;

const Circle = styled.span`
  display: inline-block;
  width: 1em;
  height: 1em;
  border: 2px solid #222;
  border-radius: 50%;
  margin-right: 0.6em;
  background: ${({ filled }) => (filled ? '#222' : 'transparent')};
  transition: background 0.18s;
`;

const MoreButton = styled.button`
  background: #222;
  color: #fff;
  font-size: 1.25rem;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  padding: 0.7rem 2.1rem;
  margin-top: 1.7rem;
  cursor: pointer;
  transition: background 0.18s;
  &:hover {
    background: #444;
  }
`;

const ClientsRow = styled.div`
  display: flex;
  gap: 2rem;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin: 2rem 0 3rem 0;
`;

const ClientLogo = styled.div`
  width: 120px;
  height: 48px;
  background: ${COLORS.offWhite};
  border-radius: 0.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  font-size: 1.1rem;
  color: #222;
`;

const WorkGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  gap: 2rem;
  margin: 2rem 0 3rem 0;
`;

const WorkCard = styled.div`
  background: ${COLORS.offWhite};
  border-radius: 1rem;
  min-height: 220px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-end;
  padding: 1.5rem;
  font-size: 1.1rem;
  font-weight: 500;
  color: #222;
`;

const Home = () => {
  const serviceNames = [
    'Custom AI',
    'Development',
    'Automation',
    'Production',
    'Design',
  ];
  const [hoveredIdx, setHoveredIdx] = React.useState(null);
  return (
    <>
      <Hero />
      <Layout>
        <Section>
          <ServicesBlock>
            <BigHeading>We do</BigHeading>
            <ServicesList>
              {serviceNames.map((name, idx) => (
                <li key={name} style={{margin: '0.7rem 0'}}>
                  <ServiceButton
                    type="button"
                    tabIndex={0}
                    onMouseEnter={() => setHoveredIdx(idx)}
                    onMouseLeave={() => setHoveredIdx(null)}
                    onFocus={() => setHoveredIdx(idx)}
                    onBlur={() => setHoveredIdx(null)}
                  >
                    <Circle filled={hoveredIdx === idx} aria-hidden="true" />
                    {name}
                  </ServiceButton>
                </li>
              ))}
            </ServicesList>
          </ServicesBlock>
        </Section>
        <Section>
          {/* Testimonial removed as requested */}
        </Section>
        <Section>
          {/* Contact section removed as requested */}
        </Section>
      </Layout>
    </>
  );
};

export default Home;
