import React, { useRef, useLayoutEffect, useState } from 'react';
import styled from 'styled-components';

// Colors for the stripes (approximate to Bakken & Bæck)
const STRIPES = [
  '#4A90E2', // blue
  '#F5D547', // yellow
  '#E6E3F7', // light purple
  '#F44F3B', // orange
  '#2D2553', // dark purple
  '#FFFCCB', // pale yellow
  '#5FC39B', // green
  '#F7D6DE', // pink
];

const HeroSection = styled.section`
  width: 100vw;
  background: #fff;
  display: flex;
  flex-direction: column;
  align-items: stretch;
  justify-content: flex-end;
  padding: 0;
  margin: 0;
  position: relative;
  overflow-x: hidden;
  min-height: 100vh;
`;

const VideoPlaceholder = styled.div`
  width: 100vw;
  height: ${({ height }) => height ? `${height}px` : '60vh'};
  display: flex;
  flex-direction: row;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  border-bottom: 0;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
`;

const Stripe = styled.div`
  flex: 1 1 0;
  height: 100%;
`;

const HeroTextBlock = styled.div`
  width: 100vw;
  background: #fff;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  padding: 0;
  margin: 0;
  position: absolute;
  left: 0;
  right: 0;
  bottom: 30px;
`;

const HeroTextInner = styled.div`
  font-family: 'Inter', 'system-ui', 'Arial', sans-serif;
  font-size: clamp(2.1rem, 4vw, 3.2rem);
  font-weight: 400;
  color: #222;
  line-height: 1.13;
  padding: 1.1rem 0 1.2rem 2.5vw;
  text-align: left;
  @media (max-width: 600px) {
    font-size: 1.3rem;
    padding-left: 4vw;
    padding-right: 2vw;
  }
`;

const BrandSpan = styled.span`
  color: #A6A6A6;
  font-weight: 700;
`;

const AmpersandSpan = styled.span`
  color: #A6A6A6;
  font-weight: 700;
  font-family: inherit;
`;

const HeroSubText = styled.span`
  color: #b3b3b3;
`;

const NAVBAR_HEIGHT = 64 + 16; // 64px min + 16px margin/padding fudge

const Hero = () => {
  const textRef = useRef(null);
  const [stripeHeight, setStripeHeight] = useState(null);

  useLayoutEffect(() => {
    function updateHeight() {
      const textBlock = textRef.current;
      const textHeight = textBlock ? textBlock.offsetHeight : 0;
      const vh = window.innerHeight;
      setStripeHeight(vh - textHeight - NAVBAR_HEIGHT);
    }
    updateHeight();
    window.addEventListener('resize', updateHeight);
    return () => window.removeEventListener('resize', updateHeight);
  }, []);

  return (
    <HeroSection>
      <VideoPlaceholder height={stripeHeight} aria-label="Video placeholder">
        {STRIPES.map((color, i) => (
          <Stripe key={i} style={{ background: color }} />
        ))}
      </VideoPlaceholder>
      <HeroTextBlock ref={textRef}>
        <HeroTextInner>
          Hepta builds private software, custom interfaces, and local AI systems. <HeroSubText>Made on your own terms.</HeroSubText>
        </HeroTextInner>
      </HeroTextBlock>
    </HeroSection>
  );
};

export default Hero;
