import React from 'react';
import Layout from '../components/Layout';
import styled from 'styled-components';
import { COLORS } from '../styles/colors';

const ColorBlocks = styled.div`
  display: flex;
  gap: 2rem;
  margin: 3rem 0 2rem 0;
  flex-wrap: wrap;
`;

const Block = styled.div`
  flex: 1 1 220px;
  min-width: 180px;
  height: 120px;
  border-radius: 1rem;
  background: ${({ color }) => color};
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${({ color }) => (color === COLORS.offWhite ? COLORS.darkTeal : COLORS.white)};
  font-size: 1.3rem;
  font-weight: 600;
  box-shadow: 0 2px 12px rgba(24,75,84,0.06);
`;

const Home = () => (
  <Layout hero={<>
    <h1 style={{fontSize: '2.5rem', margin: 0, fontWeight: 700}}>We design & build digital products</h1>
    <p style={{fontSize: '1.25rem', marginTop: '1rem', maxWidth: 600, textAlign: 'center', color: COLORS.offWhite}}>
      From strategy to launch, we help you create, elevate, and automate with design, development, and AI.
    </p>
  </>}>
    <ColorBlocks>
      <Block color={COLORS.darkTeal}>Design</Block>
      <Block color={COLORS.red}>Development</Block>
      <Block color={COLORS.offWhite}>AI & Automation</Block>
    </ColorBlocks>
    <section>
      <h2>Our Approach</h2>
      <p style={{maxWidth: 700}}>
        We combine design, technology, and AI to help companies explore opportunities, build new products, and elevate existing ones. Our team works closely with you from idea to launch, ensuring every detail is crafted for impact.
      </p>
    </section>
  </Layout>
);

export default Home;
