import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const FormWrapper = styled.div`
  max-width: 420px;
  margin: 8rem auto 0 auto;
  background: #fff;
  border-radius: 18px;
  box-shadow: 0 4px 32px 0 rgba(24,75,84,0.07);
  padding: 2.5rem 2.2rem 2.2rem 2.2rem;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const StepTitle = styled.h2`
  font-size: 1.35rem;
  font-weight: 500;
  color: #222;
  margin: 0 0 1.2rem 0;
`;

const Input = styled.input`
  width: 100%;
  font-size: 1.13rem;
  padding: 0.9rem 1.1rem;
  border: 1.5px solid #e0e0e0;
  border-radius: 7px;
  margin-bottom: 1.7rem;
  background: #fafbfa;
  color: #222;
  font-family: inherit;
  &:focus {
    outline: none;
    border-color: #5FC39B;
    background: #fff;
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  font-size: 1.13rem;
  padding: 0.9rem 1.1rem;
  border: 1.5px solid #e0e0e0;
  border-radius: 7px;
  margin-bottom: 1.7rem;
  background: #fafbfa;
  color: #222;
  font-family: inherit;
  min-height: 110px;
  resize: vertical;
  &:focus {
    outline: none;
    border-color: #5FC39B;
    background: #fff;
  }
`;

const Button = styled.button`
  background: #222;
  color: #fff;
  font-size: 1.13rem;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  padding: 1rem 2.2rem;
  margin-top: 0.5rem;
  cursor: pointer;
  transition: background 0.18s;
  &:hover {
    background: #184B54;
  }
`;

const Progress = styled.div`
  font-size: 0.98rem;
  color: #b3b3b3;
  margin-bottom: 1.2rem;
`;

const Confirmation = styled.div`
  font-size: 1.18rem;
  color: #184B54;
  margin-top: 2.5rem;
  text-align: center;
`;

const steps = [
  {
    label: 'What is your company name?',
    type: 'text',
    name: 'company',
    placeholder: 'Company name',
  },
  {
    label: stepData => `How many employees does ${stepData.company || 'your company'} have?`,
    type: 'number',
    name: 'employees',
    placeholder: 'Number of employees',
  },
  {
    label: 'Tell us about your project.',
    type: 'textarea',
    name: 'project',
    placeholder: 'Describe your project',
  },
  {
    label: 'What is the best way to get in touch with you? (Please enter your email address.)',
    type: 'email',
    name: 'email',
    placeholder: 'you@email.com',
  },
];

export default function Contact() {
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({ company: '', employees: '', project: '', email: '' });
  const [submitted, setSubmitted] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleNext = e => {
    e.preventDefault();
    if (step < steps.length - 1) {
      setStep(step + 1);
    } else {
      setSubmitted(true);
    }
  };

  if (submitted) {
    return (
      <FormWrapper>
        <Confirmation>
          Thank you for reaching out!<br />
          We'll get back to you soon.
        </Confirmation>
        <Button style={{marginTop: '2.5rem'}} onClick={() => navigate('/')}>Back to Home</Button>
      </FormWrapper>
    );
  }

  const currentStep = steps[step];
  const label = typeof currentStep.label === 'function' ? currentStep.label(form) : currentStep.label;

  return (
    <FormWrapper as="form" onSubmit={handleNext} autoComplete="off">
      <Progress>Step {step + 1} of {steps.length}</Progress>
      <StepTitle>{label}</StepTitle>
      {currentStep.type === 'textarea' ? (
        <TextArea
          name={currentStep.name}
          value={form[currentStep.name]}
          onChange={handleChange}
          placeholder={currentStep.placeholder}
          required
        />
      ) : (
        <Input
          type={currentStep.type}
          name={currentStep.name}
          value={form[currentStep.name]}
          onChange={handleChange}
          placeholder={currentStep.placeholder}
          required
        />
      )}
      <Button type="submit">{step === steps.length - 1 ? 'Submit' : 'Next'}</Button>
    </FormWrapper>
  );
}
