import React from 'react';
import styled from 'styled-components';
import { COLORS } from '../styles/colors';



const JobSection = styled.section`
  max-width: 600px;
  margin: 7rem auto 0 auto;
  padding: 4rem 0;
`;

const JobSectionHeading = styled.h2`
  font-size: clamp(1.4rem, 3vw, 2.2rem);
  color: #b3b3b3;
  font-weight: 600;
  margin-bottom: 0.4em;
  text-align: left;
  margin-left: 1px;
`;

const TopLine = styled.div`
  width: 100%;
  height: 1px;
  background: #222;
  margin-bottom: 2rem;
`;

const JobList = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const JobItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem 0;
  border-bottom: 1px solid #222;
  cursor: pointer;
  transition: background 0.18s;

  &:hover {
    background: #f8f8f8;
  }

  &:last-child {
    border-bottom: 1px solid #222;
  }
`;

const JobTitle = styled.h3`
  font-size: 1.7rem;
  font-weight: 500;
  color: #222;
  margin: 0;
  line-height: 1.15;
`;

const ArrowIcon = styled.span`
  font-size: 1.2rem;
  color: #222;
  font-weight: 400;
  transition: transform 0.2s ease;
  transform: ${({ expanded }) => expanded ? 'rotate(180deg)' : 'rotate(0deg)'};
`;

const JobContent = styled.div`
  padding: 0 0 1.5rem 0;
  border-bottom: 1px solid #222;
`;

const JobDescription = styled.p`
  font-size: 1rem;
  color: #666;
  line-height: 1.6;
  margin: 0 0 1.5rem 0;
  max-width: 500px;
`;

const JobLink = styled.a`
  color: #222;
  text-decoration: underline;
  font-size: 1rem;
  font-weight: 500;
  transition: color 0.2s ease;

  &:hover {
    color: #666;
  }
`;



const CTAButton = styled.button`
  background: #222;
  color: #fff;
  font-size: 1.1rem;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  padding: 1rem 2rem;
  cursor: pointer;
  transition: background 0.2s ease;
  margin-top: 2rem;

  &:hover {
    background: #444;
  }
`;

const Careers = () => {
  const [expandedJob, setExpandedJob] = React.useState(null);

  const jobs = [
    {
      title: 'Creative Lead',
      description: 'We are looking for a creative leader who can inspire and guide our design team. You will be responsible for developing creative strategies, mentoring designers, and ensuring our work meets the highest standards.',
      link: 'https://example.com/creative-lead'
    },
    {
      title: 'Lead Product Designer',
      description: 'Join us as a Lead Product Designer to create exceptional user experiences. You will work closely with product managers and developers to design intuitive, beautiful, and functional digital products.',
      link: 'https://example.com/lead-product-designer'
    },
    {
      title: 'Senior Creative Strategist',
      description: 'As a Senior Creative Strategist, you will develop innovative creative strategies that drive business results. You will collaborate with clients and internal teams to deliver compelling creative solutions.',
      link: 'https://example.com/senior-creative-strategist'
    },
    {
      title: 'Senior Motion Designer',
      description: 'We are seeking a Senior Motion Designer to bring our designs to life through animation and motion graphics. You will create engaging visual experiences that enhance user interactions and brand storytelling.',
      link: 'https://example.com/senior-motion-designer'
    },
    {
      title: 'Senior Product Manager',
      description: 'Join our product team as a Senior Product Manager. You will lead product strategy, work with cross-functional teams, and ensure we deliver products that users love and that drive business growth.',
      link: 'https://example.com/senior-product-manager'
    },
    {
      title: 'Open application',
      description: 'Do not see a position that fits? We are always looking for talented individuals to join our team. Send us your application and let us know how you can contribute to our mission.',
      link: 'https://example.com/open-application'
    }
  ];



  return (
    <>
      <JobSection>
        <JobSectionHeading>Open positions</JobSectionHeading>
        <TopLine />
        <JobList>
          {jobs.map((job, index) => (
            <div key={index}>
              <JobItem onClick={() => setExpandedJob(expandedJob === index ? null : index)}>
                <JobTitle>{job.title}</JobTitle>
                <ArrowIcon expanded={expandedJob === index}>↓</ArrowIcon>
              </JobItem>
              {expandedJob === index && (
                <JobContent>
                  <JobDescription>{job.description}</JobDescription>
                  <JobLink href={job.link} target="_blank" rel="noopener noreferrer">
                    View full listing →
                  </JobLink>
                </JobContent>
              )}
            </div>
          ))}
        </JobList>
        <CTAButton>Get in touch</CTAButton>
      </JobSection>
    </>
  );
};

export default Careers;
