    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const PreviewSection = styled.div`
  margin-top: 3rem;
  padding-top: 2rem;
  border-top: 1px solid #f0f0f0;
`;

const PreviewTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: #222;
  margin-bottom: 1rem;
`;

const PreviewCard = styled.div`
  background: white;
  border: 1px solid #f0f0f0;
  border-radius: 8px;
  padding: 1.5rem;
  margin-bottom: 1rem;
  width: 100%;
  max-width: 280px;
  min-height: 320px;
`;

const PreviewImage = styled.div`
  display: none;
`;

const PreviewDate = styled.div`
  font-size: 0.9rem;
  color: #b3b3b3;
  margin-bottom: 0.5rem;
  font-weight: 500;
`;

const PreviewPostTitle = styled.h3`
  font-size: 1.1rem;
  font-weight: 600;
  color: #222;
  margin: 0 0 0.8rem 0;
  line-height: 1.3;
`;

const PreviewExcerpt = styled.p`
  font-size: 0.9rem;
  color: #666;
  margin: 0 0 1.5rem 0;
  line-height: 1.5;
`;

const PreviewAuthor = styled.div`
  font-size: 0.8rem;
  color: #b3b3b3;
  font-weight: 500;
`;

const BlogCMS = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    excerpt: '',
    author: '',
    image: '',
    featured: false,
    content: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleToolbarClick = (format) => {
    const textarea = document.getElementById('content-editor');
    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const text = textarea.value;

    let replacement = '';
    switch (format) {
      case 'h2':
        replacement = `<h2>${text.substring(start, end)}</h2>`;
        break;
      case 'h3':
        replacement = `<h3>${text.substring(start, end)}</h3>`;
        break;
      case 'p':
        replacement = `<p>${text.substring(start, end)}</p>`;
        break;
      case 'ul':
        replacement = `<ul>\n  <li>${text.substring(start, end)}</li>\n</ul>`;
        break;
      case 'blockquote':
        replacement = `<blockquote>${text.substring(start, end)}</blockquote>`;
        break;
      default:
        return;
    }

    const newText = text.substring(0, start) + replacement + text.substring(end);
    setFormData(prev => ({ ...prev, content: newText }));

    // Focus back on textarea
    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(start, start + replacement.length);
    }, 0);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate API call
    setTimeout(() => {
      // In a real app, you'd send this to your backend
      console.log('Saving blog post:', formData);

      // Generate a slug from the title
      const slug = formData.title
        .toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')
        .replace(/(^-|-$)/g, '');

      setIsSubmitting(false);
      navigate(`/blog/${slug}`);
    }, 1000);
  };

  const handlePreview = () => {
    // In a real app, you might want to show a modal or navigate to a preview page
    console.log('Preview:', formData);
  };

  return (
    <CMSWrapper>
      <CMSSection>
        <CMSContainer>
          <BackButton to="/blog">← Back to Blog</BackButton>

          <CMSTitle>Create New Blog Post</CMSTitle>

          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label htmlFor="title">Title *</Label>
              <Input
                id="title"
                name="title"
                type="text"
                value={formData.title}
                onChange={handleInputChange}
                required
                placeholder="Enter the blog post title"
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="excerpt">Excerpt *</Label>
              <TextArea
                id="excerpt"
                name="excerpt"
                value={formData.excerpt}
                onChange={handleInputChange}
                required
                placeholder="Brief description of the blog post"
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="author">Author *</Label>
              <Input
                id="author"
                name="author"
                type="text"
                value={formData.author}
                onChange={handleInputChange}
                required
                placeholder="Author name"
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="image">Featured Image URL</Label>
              <Input
                id="image"
                name="image"
                type="url"
                value={formData.image}
                onChange={handleInputChange}
                placeholder="https://example.com/image.jpg"
              />
            </FormGroup>

            <FormGroup>
              <CheckboxGroup>
                <Checkbox
                  id="featured"
                  name="featured"
                  type="checkbox"
                  checked={formData.featured}
                  onChange={handleInputChange}
                />
                <Label htmlFor="featured">Featured Post</Label>
              </CheckboxGroup>
            </FormGroup>

            <FormGroup>
              <Label>Content *</Label>
              <ContentEditor>
                <EditorToolbar>
                  <ToolbarButton type="button" onClick={() => handleToolbarClick('h2')}>
                    H2
                  </ToolbarButton>
                  <ToolbarButton type="button" onClick={() => handleToolbarClick('h3')}>
                    H3
                  </ToolbarButton>
                  <ToolbarButton type="button" onClick={() => handleToolbarClick('p')}>
                    P
                  </ToolbarButton>
                  <ToolbarButton type="button" onClick={() => handleToolbarClick('ul')}>
                    List
                  </ToolbarButton>
                  <ToolbarButton type="button" onClick={() => handleToolbarClick('blockquote')}>
                    Quote
                  </ToolbarButton>
                </EditorToolbar>
                <EditorContent
                  id="content-editor"
                  name="content"
                  value={formData.content}
                  onChange={handleInputChange}
                  required
                  placeholder="Write your blog post content here. Use the toolbar above to format your text."
                />
              </ContentEditor>
            </FormGroup>

            <ButtonGroup>
              <Button type="submit" className="primary" disabled={isSubmitting}>
                {isSubmitting ? 'Publishing...' : 'Publish Post'}
              </Button>
              <Button type="button" className="secondary" onClick={handlePreview}>
                Preview
              </Button>
            </ButtonGroup>
          </Form>

                     {formData.title && (
             <PreviewSection>
               <PreviewTitle>Preview</PreviewTitle>
               <PreviewCard>
                 <PreviewDate>{new Date().toLocaleDateString('en-US', { 
                   year: 'numeric', 
                   month: 'long', 
                   day: 'numeric' 
                 })}</PreviewDate>
                 <PreviewPostTitle>{formData.title}</PreviewPostTitle>
                 <PreviewExcerpt>{formData.excerpt}</PreviewExcerpt>
                 <PreviewAuthor>By {formData.author}</PreviewAuthor>
               </PreviewCard>
             </PreviewSection>
           )}
        </CMSContainer>
      </CMSSection>
    </CMSWrapper>
  );
};

export default BlogCMS;
