import React from 'react';
import styled, { keyframes, css } from 'styled-components';
import { COLORS } from '../styles/colors';

const HeroTextBlock = styled.div`
  width: 100vw;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin: 0 0 2.5rem 0;
`;

const HeroTextInner = styled.div`
  font-family: 'Inter', 'system-ui', 'Arial', sans-serif;
  font-size: clamp(2.1rem, 4vw, 3.2rem);
  font-weight: 400;
  color: #222;
  line-height: 1.13;
  padding: 1.1rem 0 1.2rem 2.5vw;
  text-align: left;
  max-width: 1200px;
  @media (max-width: 600px) {
    font-size: 1.3rem;
    padding-left: 4vw;
    padding-right: 2vw;
  }
`;

const HeroSubText = styled.span`
  color: #b3b3b3;
`;

const Section = styled.section`
  max-width: 700px;
  margin: 9rem auto 0 auto;
  padding: 0 15px;
`;

const SectionHeading = styled.h2`
  font-size: 1.35rem;
  font-weight: 700;
  color: #222;
  margin: 0 0 1.2rem 0;
`;

const SectionText = styled.p`
  font-size: 1.18rem;
  color: #222;
  margin: 0 0 2.2rem 0;
  line-height: 1.6;
`;

const WorkGridWrapper = styled.div`
  width: 100vw;
  position: relative;
  left: 50%;
  margin-left: -50vw;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 32rem;
  margin-bottom: 5rem;
  @media (max-width: 700px) {
    margin-top: 16rem;
    margin-bottom: 2.5rem;
  }
`;

const WorkGridSection = styled.section`
  margin: 4rem 0 0 0;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ImagePlaceholderBox = styled.div`
  width: 100%;
  max-width: 1200px;
  height: 320px;
  background: #eee;
  border-radius: 3px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #bbb;
  font-size: 1.3rem;
  font-weight: 500;
  margin-left: 2.5vw;
  margin-bottom: 2.5rem;
  box-sizing: border-box;
`;

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(16px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pushUp = keyframes`
  from {
    transform: translateY(18px);
  }
  to {
    transform: translateY(0);
  }
`;

const CardGrid = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.4rem;
  margin: 0 auto 7rem auto;
  max-width: 600px;
  width: 100%;
`;

const Card = styled.div`
  display: flex;
  align-items: center;
  background: ${({ active }) => (active ? COLORS.green : '#F5F5F5')};
  border-radius: 2px;
  padding: 2.2rem 2.5rem;
  height: 135px;
  box-sizing: border-box;
  transition: background 0.22s cubic-bezier(0.4, 0.2, 0.2, 1);
  margin: 1.1rem 0 0 0;
  cursor: pointer;
  width: 100%;
  max-width: 600px;
  overflow: hidden;
  @media (max-width: 700px) {
    flex-direction: column;
    align-items: flex-start;
    padding: 1.2rem 1rem;
    height: 160px;
  }
`;

const CardContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const CardTitle = styled.h3`
  font-size: 1.13rem;
  font-weight: 600;
  margin: 0 0 0.7rem 0;
  ${({ active }) =>
    active &&
    css`
      animation: ${pushUp} 0.22s cubic-bezier(0.4, 0.2, 0.2, 1);
    `}
`;

const CardDesc = styled.p`
  font-size: 1rem;
  margin: 0;
  min-height: 3.6em;
  max-height: 3.6em;
  overflow: hidden;
  transition: color 0.22s cubic-bezier(0.4, 0.2, 0.2, 1);
  animation: ${fadeInUp} 0.22s cubic-bezier(0.4, 0.2, 0.2, 1);
`;

const ABOUT_CARDS = [
  {
    title: 'Custom AI',
    desc: 'AI that fits your business, not the other way around. Full control, private, and tailored to your needs.'
  },
  {
    title: 'Development',
    desc: 'Modern web and software, built for speed, scale, and security.'
  },
  {
    title: 'Automation',
    desc: 'Automate the boring stuff. Integrate, streamline, and focus on what matters.'
  },
  {
    title: 'Production',
    desc: 'Content and video, from idea to launch, with technical and creative excellence.'
  },
  {
    title: 'Design',
    desc: 'Digital experiences that are beautiful, intuitive, and effective.'
  },
];

const About = () => {
  const [openCard, setOpenCard] = React.useState(0);
  return (
    <>
      <WorkGridWrapper>
        <WorkGridSection>
          <HeroTextBlock>
            <HeroTextInner>
              We're builders, designers, and engineers <HeroSubText>working</HeroSubText> across AI, software, and content.
            </HeroTextInner>
            <ImagePlaceholderBox>[image]</ImagePlaceholderBox>
          </HeroTextBlock>
        </WorkGridSection>
      </WorkGridWrapper>
      <CardGrid>
        {[0,1,2,3,4].map((idx) => (
          <React.Fragment key={idx}>
            <Section>
              <SectionHeading>{ABOUT_CARDS[idx].title}</SectionHeading>
              <SectionText>
                {[
                  'We design and build AI solutions tailored to your business, giving you full control over your data, models, and deployment. From private LLMs to workflow automation, we help you unlock the power of AI on your terms.',
                  'Our engineering team delivers robust, scalable web and software solutions. We handle everything from backend APIs to modern frontends, ensuring your product is fast, secure, and ready to grow.',
                  'We automate repetitive tasks and complex workflows, integrating your tools and data so you can focus on what matters. Our automations are reliable, secure, and built to fit your unique needs.',
                  'From video to digital content, we help you create, launch, and scale your brand\'s story. Our production team brings ideas to life with creativity, speed, and technical excellence.',
                  'We craft digital experiences that are beautiful, intuitive, and effective. Our design team works closely with you to turn ideas into products people love to use.'
                ][idx]}
              </SectionText>
            </Section>
            <Card
              active={openCard === idx}
              onMouseEnter={() => setOpenCard(idx)}
              onFocus={() => setOpenCard(idx)}
              tabIndex={0}
              aria-label={ABOUT_CARDS[idx].title}
            >
              <CardContent>
                <CardTitle active={openCard === idx}>{ABOUT_CARDS[idx].title}</CardTitle>
                {openCard === idx && <CardDesc>{ABOUT_CARDS[idx].desc}</CardDesc>}
              </CardContent>
            </Card>
          </React.Fragment>
        ))}
      </CardGrid>
    </>
  );
};

export default About;
