import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';

const NAV_GREEN = '#1ED760';
const NAV_GROUP_BORDER = '1.5px solid ' + NAV_GREEN;
const NAV_MONO = 'Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace';

const NavbarContainer = styled.nav`
  width: 100vw;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 100;
  background: transparent;
  border: none;
  box-shadow: none;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  font-family: ${NAV_MONO};
  min-height: 64px;
  pointer-events: none;
`;

const NavGroup = styled.div`
  display: flex;
  align-items: center;
  background: rgba(255,255,255,0.85);
  border: ${NAV_GROUP_BORDER};
  border-radius: 12px;
  padding: 0.18rem 1.1rem 0.18rem 0.4rem;
  margin: 1.1rem 0 0 2.1rem;
  box-shadow: none;
  pointer-events: auto;
`;

const Logo = styled(Link)`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 34px;
  height: 34px;
  border-radius: 50%;
  border: ${NAV_GROUP_BORDER};
  background: #fff;
  color: #222;
  font-family: ${NAV_MONO};
  font-size: 1.05rem;
  font-weight: 700;
  margin-right: 0.9rem;
  text-decoration: none;
  letter-spacing: 0.04em;
  transition: border 0.2s;
`;

const NavLinks = styled.ul`
  display: flex;
  align-items: center;
  gap: 1.05rem;
  list-style: none;
  margin: 0;
  padding: 0;
`;

const NavLink = styled.li`
  a {
    text-decoration: none;
    color: #222;
    font-family: ${NAV_MONO};
    font-size: 0.98rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.13em;
    padding: 0.08rem 0.18rem;
    border-radius: 4px;
    transition: color 0.18s;
    &:hover, &:focus {
      color: ${NAV_GREEN};
      background: none;
      outline: none;
    }
    &.active {
      color: ${NAV_GREEN};
    }
  }
`;

const NAV_LINKS = [
  { to: '/work', label: 'Work' },
  { to: '/about', label: 'About' },
  { to: '/journal', label: 'Journal' },
  { to: '/clients', label: 'Clients' },
  { to: '/join', label: 'Join us' },
];

const Navbar = () => {
  const location = useLocation();
  return (
    <NavbarContainer role="navigation" aria-label="Main Navigation">
      <NavGroup>
        <Logo to="/">BB</Logo>
        <NavLinks>
          {NAV_LINKS.map((link) => (
            <NavLink key={link.to}>
              <Link
                to={link.to}
                className={location.pathname === link.to ? 'active' : ''}
                tabIndex={0}
                aria-current={location.pathname === link.to ? 'page' : undefined}
              >
                {link.label}
              </Link>
            </NavLink>
          ))}
        </NavLinks>
      </NavGroup>
    </NavbarContainer>
  );
};

export default Navbar;
