import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const CenteredPage = styled.div`
  min-height: 100vh;
  width: 100vw;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #fafbfa;
`;

const FormWrapper = styled.div`
  width: 420px;
  min-height: 320px;
  margin: 0 auto;
  /* background: #fff; */
  /* border-radius: 18px; */
  /* box-shadow: 0 4px 32px 0 rgba(24,75,84,0.07); */
  padding: 2.5rem 2.2rem 2.2rem 2.2rem;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  box-sizing: border-box;
`;

const StepTitle = styled.h2`
  font-size: 1.35rem;
  font-weight: 500;
  color: #222;
  margin: 0 0 1.2rem 0;
`;

const Input = styled.input`
  width: 100%;
  font-size: 1.13rem;
  padding: 0.9rem 1.1rem 0.4rem 1.1rem;
  border: none;
  border-bottom: 2px solid #e0e0e0;
  border-radius: 0;
  margin-bottom: 1.7rem;
  background: transparent;
  color: #222;
  font-family: inherit;
  box-shadow: none;
  box-sizing: border-box;
  &:focus {
    outline: none;
    border-bottom: 2px solid #e0e0e0;
    background: transparent;
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  font-size: 1.13rem;
  padding: 0.9rem 1.1rem 0.4rem 1.1rem;
  border: none;
  border-bottom: 2px solid #e0e0e0;
  border-radius: 0;
  margin-bottom: 1.7rem;
  background: transparent;
  color: #222;
  font-family: inherit;
  min-height: 110px;
  resize: vertical;
  box-shadow: none;
  box-sizing: border-box;
  &:focus {
    outline: none;
    border-bottom: 2px solid #e0e0e0;
    background: transparent;
  }
`;

const Button = styled.button`
  background: none;
  color: #222;
  font-size: 1.13rem;
  font-weight: 600;
  border: none;
  border-radius: 0;
  padding: 0;
  margin-top: 0.5rem;
  cursor: pointer;
  transition: color 0.18s;
  box-shadow: none;
  &:hover {
    color: #184B54;
  }
`;
